// Filter only PDF and image attachments - Updated for Gmail header format
const items = [];

// Helper function to extract value from header string
function extractHeader(headerString) {
  if (!headerString) return '';
  // Remove prefix like "Subject: ", "From: ", etc.
  const parts = headerString.split(': ');
  return parts.length > 1 ? parts.slice(1).join(': ').trim() : headerString.trim();
}

for (const item of $input.all()) {
  // Get email metadata from headers
  const headers = item.json.headers || {};
  const emailId = item.json.id;
  const emailSubject = extractHeader(headers.subject);
  const emailFrom = extractHeader(headers.from);
  const emailTo = extractHeader(headers.to);
  const emailDate = extractHeader(headers.date);

  console.log('Processing email:', emailSubject);
  console.log('From:', emailFrom);
  console.log('Binary data available:', item.binary ? 'YES' : 'NO');

  // Check if there are binary attachments
  if (!item.binary || Object.keys(item.binary).length === 0) {
    console.log('WARNING: No attachments found. Make sure "Download Attachments" is enabled!');
    continue;
  }

  // Get all binary keys
  const binaryKeys = Object.keys(item.binary);
  console.log('Binary keys found:', binaryKeys);

  // Loop through all binary attachments
  for (const key of binaryKeys) {
    const attachment = item.binary[key];

    if (!attachment) {
      console.log(`Skipping empty attachment: ${key}`);
      continue;
    }

    const fileName = attachment.fileName || key;
    const mimeType = attachment.mimeType || '';
    const fileSize = attachment.fileSize || 0;

    console.log(`Checking: ${fileName} (${mimeType}, ${fileSize} bytes)`);

    // Check if it's a PDF or image
    const isPDF = mimeType.includes('pdf') || /\.pdf$/i.test(fileName);
    const isImage = mimeType.includes('image') || /\.(png|jpg|jpeg|gif|bmp|tiff)$/i.test(fileName);

    if (isPDF || isImage) {
      console.log(`‚úì Valid invoice attachment: ${fileName}`);

      items.push({
        json: {
          emailId: emailId,
          emailSubject: emailSubject,
          emailFrom: emailFrom,
          emailTo: emailTo,
          emailDate: emailDate,
          fileName: fileName,
          mimeType: mimeType,
          fileSize: fileSize,
          attachmentKey: key,
          fileType: isPDF ? 'pdf' : 'image'
        },
        binary: {
          data: attachment
        }
      });
    } else {
      console.log(`‚úó Skipping non-invoice file: ${fileName}`);
    }
  }
}

// Handle no attachments found
if (items.length === 0) {
  const firstItem = $input.first();
  const headers = firstItem.json.headers || {};

  console.log('No PDF or image attachments found');

  return [{
    json: {
      message: 'No invoice attachments found',
      emailSubject: extractHeader(headers.subject),
      emailFrom: extractHeader(headers.from),
      hasBinaryData: !!firstItem.binary,
      binaryKeys: firstItem.binary ? Object.keys(firstItem.binary) : []
    }
  }];
}

console.log(`‚úì Successfully found ${items.length} invoice attachment(s)`);
return items;

// Clean and parse AI response to valid JSON
let response =  $input.first().json.output|| $json.text || JSON.stringify($json);

console.log('=== RAW AI RESPONSE ===');
console.log(response.substring(0, 500)); // Show first 500 chars

// Clean up common issues
response = response.trim();

// Remove markdown code blocks if present
response = response.replace(/```json\n?/gi, '');
response = response.replace(/```\n?/g, '');

// Remove any text before first { or after last }
const firstBrace = response.indexOf('{');
const lastBrace = response.lastIndexOf('}');

if (firstBrace !== -1 && lastBrace !== -1) {
  response = response.substring(firstBrace, lastBrace + 1);
} else {
  console.error('‚ùå No valid JSON braces found in response');
  return [{
    json: {
      parseError: true,
      errorMessage: 'No valid JSON structure found',
      rawResponse: response.substring(0, 1000),
      originalEmail: {
        id: $('Filter Invoice Attachments').first().json.emailId,
        subject: $('Filter Invoice Attachments').first().json.emailSubject,
        from: $('Filter Invoice Attachments').first().json.emailFrom,
        fileName: $('Filter Invoice Attachments').first().json.fileName
      }
    }
  }];
}

// Try to parse JSON
try {
  const data = JSON.parse(response);
  console.log('‚úì Successfully parsed invoice data');
  console.log('Invoice Number:', data.invoice_number);
  console.log('Vendor:', data.vendor_name);
  console.log('Total:', data.total_amount, data.currency);

  return [{
    json: {
      ...data,
      originalEmail: {
        id: $('Filter Invoice Attachments').item.json.emailId,
        subject: $('Filter Invoice Attachments').item.json.emailSubject,
        from: $('Filter Invoice Attachments').item.json.emailFrom,
        date: $('Filter Invoice Attachments').item.json.emailDate,
        fileName: $('Filter Invoice Attachments').item.json.fileName
      }
    }
  }];

} catch (error) {
  console.error('‚ùå JSON Parse Error:', error.message);
  console.error('Problematic JSON:', response.substring(0, 500));

  // Return error for manual review
  return [{
    json: {
      parseError: true,
      errorMessage: error.message,
      rawResponse: response,
      originalEmail: {
        id: $('Filter Invoice Attachments').item.json.emailId,
        subject: $('Filter Invoice Attachments').item.json.emailSubject,
        from: $('Filter Invoice Attachments').item.json.emailFrom,
        fileName: $('Filter Invoice Attachments').item.json.fileName
      }
    }
  }];
}

// Validate and enrich extracted invoice data

// Check for parse errors first
if ($json.parseError) {
  console.error('Parse error detected, returning invalid result');
  return [{
    json: {
      validation: {
        isValid: false,
        errors: ['AI failed to extract data: ' + $json.errorMessage],
        warnings: ['Raw response: ' + ($json.rawResponse || '').substring(0, 200)]
      },
      originalEmail: $json.originalEmail
    }
  }];
}

const extractedData = $json;

// Validation results
const validation = {
  isValid: true,
  errors: [],
  warnings: []
};

// Required field validation
const requiredFields = {
  'invoice_number': 'Invoice Number',
  'invoice_date': 'Invoice Date',
  'vendor_name': 'Vendor Name',
  'total_amount': 'Total Amount',
  'currency': 'Currency'
};

for (const [field, label] of Object.entries(requiredFields)) {
  if (!extractedData[field]) {
    validation.isValid = false;
    validation.errors.push(`Missing required field: ${label}`);
  }
}

// Date validation and parsing
const validateDate = (dateStr, fieldName) => {
  if (!dateStr) return null;

  const date = new Date(dateStr);
  if (isNaN(date.getTime())) {
    validation.errors.push(`Invalid date format for ${fieldName}: ${dateStr}`);
    validation.isValid = false;
    return null;
  }
  return date;
};

const invoiceDate = validateDate(extractedData.invoice_date, 'invoice_date');
const dueDate = validateDate(extractedData.due_date, 'due_date');

// Amount validation
let totalAmount = extractedData.total_amount;
if (totalAmount !== null && totalAmount !== undefined) {
  totalAmount = parseFloat(totalAmount);
  if (isNaN(totalAmount) || totalAmount <= 0) {
    validation.errors.push(`Invalid total amount: ${extractedData.total_amount}`);
    validation.isValid = false;
  }
} else {
  validation.isValid = false;
  validation.errors.push('Total amount is missing');
}

// Calculate days until due
let daysUntilDue = null;
let urgencyLevel = 'normal';

if (dueDate) {
  const now = new Date();
  now.setHours(0, 0, 0, 0); // Reset time for accurate day calculation
  dueDate.setHours(0, 0, 0, 0);

  daysUntilDue = Math.ceil((dueDate - now) / (1000 * 60 * 60 * 24));

  if (daysUntilDue < 0) {
    urgencyLevel = 'overdue';
    validation.warnings.push(`Invoice is ${Math.abs(daysUntilDue)} days OVERDUE!`);
  } else if (daysUntilDue === 0) {
    urgencyLevel = 'critical';
    validation.warnings.push('Invoice is due TODAY!');
  } else if (daysUntilDue <= 3) {
    urgencyLevel = 'critical';
    validation.warnings.push(`Invoice due in ${daysUntilDue} days`);
  } else if (daysUntilDue <= 7) {
    urgencyLevel = 'high';
  } else if (daysUntilDue <= 14) {
    urgencyLevel = 'medium';
  }
} else {
  validation.warnings.push('No due date specified');
}

// Amount-based urgency
let amountUrgency = 'normal';
if (totalAmount > 10000) {
  amountUrgency = 'high';
  validation.warnings.push('High-value invoice (>$10,000)');
} else if (totalAmount > 5000) {
  amountUrgency = 'medium';
  validation.warnings.push('Medium-value invoice (>$5,000)');
}

// Combined urgency (use highest)
const urgencyPriority = {
  'overdue': 5,
  'critical': 4,
  'high': 3,
  'medium': 2,
  'normal': 1
};

const finalUrgency = urgencyPriority[urgencyLevel] >= urgencyPriority[amountUrgency]
  ? urgencyLevel 
  : amountUrgency;

// Log validation summary
console.log('=== VALIDATION SUMMARY ===');
console.log('Valid:', validation.isValid);
console.log('Errors:', validation.errors.length);
console.log('Warnings:', validation.warnings.length);
console.log('Urgency:', finalUrgency);
console.log('Days Until Due:', daysUntilDue);

return [{
  json: {
    // All extracted fields
    invoice_number: extractedData.invoice_number,
    invoice_date: extractedData.invoice_date,
    due_date: extractedData.due_date,
    vendor_name: extractedData.vendor_name,
    vendor_email: extractedData.vendor_email,
    vendor_address: extractedData.vendor_address,
    customer_name: extractedData.customer_name,
    customer_address: extractedData.customer_address,
    subtotal: extractedData.subtotal,
    tax_amount: extractedData.tax_amount,
    total_amount: totalAmount,
    currency: extractedData.currency,
    line_items: extractedData.line_items || [],
    payment_terms: extractedData.payment_terms,
    notes: extractedData.notes,

    // Enriched data
    validation,
    daysUntilDue,
    urgencyLevel: finalUrgency,
    amountUrgency,
    processedDate: new Date().toISOString(),

    // Original email reference
    originalEmail: extractedData.originalEmail
  }
}];

